mitzvah(topical,597,'Not to let any of them remain alive',[[book(deut,chapter(20,verses(16)))]]).
mitzvah(topical,598,'Wipe out the descendants of Amalek',[[book(deut,chapter(25,verses(19)))]]).
mitzvah(topical,599,'Remember what Amalek did to the Jewish people',[[book(deut,chapter(25,verses(17)))]]).
mitzvah(topical,600,'Not to forget Amalek"s atrocities and ambush on our journey from Egypt in the desert',[[book(deut,chapter(25,verses(19)))]]).
mitzvah(topical,601,'Not to dwell permanently in Egypt',[[book(deut,chapter(17,verses(16)))]]).
mitzvah(topical,602,'Offer peace terms to the inhabitants of a city while holding siege, and treat them according to the Torah if they accept the terms',[[book(deut,chapter(20,verses(10)))]]).
mitzvah(topical,603,'Not to offer peace to Ammon and Moab while besieging them',[[book(deut,chapter(23,verses(7)))]]).
mitzvah(topical,604,'Not to destroy food trees even during the siege',[[book(deut,chapter(20,verses(19)))]]).
mitzvah(topical,605,'Prepare latrines outside the camps',[[book(deut,chapter(23,verses(13)))]]).
mitzvah(topical,606,'Prepare a shovel for each soldier to dig with',[[book(deut,chapter(23,verses(14)))]]).
mitzvah(topical,607,'Appoint a priest to speak with the soldiers during the war',[[book(deut,chapter(20,verses(2)))]]).
mitzvah(topical,608,'He who has taken a wife, built a new home, or planted a vineyard is given a year to rejoice with his possessions',[[book(deut,chapter(24,verses(5)))]]).
mitzvah(topical,609,'Not to demand from the above any involvement, communal or military',[[book(deut,chapter(24,verses(5)))]]).
mitzvah(topical,610,'Not to panic and retreat during battle',[[book(deut,chapter(20,verses(3)))]]).
mitzvah(topical,611,'Keep the laws of the captive woman',[[book(deut,chapter(21,verses(11)))]]).
mitzvah(topical,612,'Not to sell her into slavery',[[book(deut,chapter(21,verses(14)))]]).
mitzvah(topical,613,'Not to retain her for servitude after having sexual relations with her',[[book(deut,chapter(21,verses(14)))]]).

listAllMisformattedMitzvah(Items) :-
	findall(Item2,(mitzvah(_,_,_,Args),member(Item,Args),member(Item2,Item),(not((Item2 =.. [book,Book,Chapter],Chapter =.. [chapter,Int,Verses],Verses =.. [verses,VerseList],(integer(VerseList) ; is_list(VerseList)))),not(((Item2 =.. [yemenite,Item3] ; Item2 =.. [standard,Item3]),Item3 =.. [book,Book,Chapter],Chapter =.. [chapter,Int,Verses],Verses =.. [verses,VerseList],(integer(VerseList) ; is_list(VerseList)))))),Items).

listAllMisformattedMitzvah :-
	listAllMisformattedMitzvah(Items),write_list(Items).

